@misc{unknown-a,
  title = {During this process, the spike (S) glycoprotein of HCoV plays an important role[3]. 6 The surface unit (S1) of S protein mediates the entry into host cells by binding to 7 cellular receptor and the transmembrane unit (S2) subunit harbors the functional 8 elements for the fusion of viral and cellular membranes [4]. It should be noted that the 9 cleavage of S protein is priming to be activated which is essential for viral infectivity 10 [5]. Thus, this process not only needs virus-binding components (cell receptors), but 11 also requires virus protein-cleaving components (cell proteases)[6]. 12 In 2019-nCoV infection, a metallopeptidase, angiotensin converting enzyme II (ACE2) 13 is proved to be the cellular receptor, same as SARS-CoV infection[7, 8]. As the host 14 cell protease of ACE2, the type II transmembrane serine proteases (TTSPs), such as 15 TMPRSS2 and TMPRSS11D, can cleave and activate the SARS-CoV S protein 16 (SARS-S) for membrane fusion and also cleave ACE2 to promote viral uptake},
  note = {9, 10},
